import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/styles';
import Button from '@material-ui/core/Button';
import classNames from 'classnames';
import { Paper, Typography } from '@material-ui/core';
import Grid from '@material-ui/core/Grid';

import RadioButtonField from './radiobuttonfield';
import WeightEntry from './weightentry';
import {CONSTANTS} from '../../constants/constants';
import {FIT_GOALS} from "../../constants/constants";



const styles = (theme) => ({
  control: {
    padding: '1em',
    minHeight: '5em',
  },
  root: {
    paddingTop: '1em',
    display: 'flex',
    margin: '0 auto',
    flexWrap: 'wrap',
    maxWidth: '400px',
    marginBottom: '1em'
  },
  group: {
    margin: 'auto',
  },
  button: {
    marginTop: '.75em',
  },
  logo: {
    marginTop: '2.5em',
    marginLeft: 'auto',
    marginRight: 'auto',
    marginBottom: '.5em',
    width: '75px'
  },
  width: {
    width: '100%',
  },
  sideMargin: {
    marginLeft: '1.5em',
    marginRight: '1.5em',
  },
  ico: {
    transform: 'scale(1.75)',
  }
});

class HomePage extends React.Component {
  /*
  this can be defaulted to have the radio button 'lit'
  */
  constructor (props) {
    super(props);
    this.state = { 
      gender: 'female',
      level: 'sedentary',
      goal: 'lose',
      bodyWeight: '',
      height: '',
      units: 'kilos',
      fat: '0.0',
      carbs: '0.0',
      protein: '0.0',
      totalCalories: '0'
    }
  };

  handleChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value,
    });
    console.log(name, value);
    console.log(typeof value);

  };

  calculateScore = () => {

    if (this.state.weight === '' || this.state.total === '') return;

    var weight = Number(this.state.bodyWeight);
    var height = Number(this.state.height);
    var age = Number(this.state.age);
    var total = Number(this.state.total);

    /* Check inputs to make sure they are valid */
    if (isNaN(weight) || isNaN(total)) return;

    /* Both calculations need to be done in kg */
    if (this.state.units === 'pounds') {
      weight = this.state.bodyWeight * 0.453592;
      total = this.state.total * 0.453592;
    };

    /**
     * BMR Calories
     */
    var constants = CONSTANTS[this.state.gender][this.state.level];
    var bmr = constants[0] + (constants[1] * Math.log(weight)) + (constants[2] * Math.log(height)) - (constants[3] * age);


    /**
     * Total Calories
     */
    var C = FIT_GOALS[this.state.goal];
    var fewerCalories = bmr * C[0];
    var calories2Lose = bmr - fewerCalories;
    var calories2Gain = bmr + C[1];
    var calories2Maintain = bmr;

  };

  render() {

    const { classes } = this.props;

    return (
      <div className={classes.root}>

        <Paper
        className={classNames(classes.control, classes.sideMargin, classes.width)}>
         <Grid container spacing={24}>
          <Grid item xs={6}>
            <Typography align="center" variant="h6" color="secondary">
              {"Total Calories:"}
            </Typography>
            <Typography align="center" variant="h4" color="primary">
              {this.state.totalCalories}
            </Typography>
          </Grid>
          <Grid item xs={6}>
            <Typography align="center" variant="h6" color="secondary">
              {"Carbs:"}
            </Typography>
            <Typography align="center" variant="h4" color="primary">
              {this.state.wilksScore}
            </Typography>
          </Grid>
          </Grid>
        </Paper>

        <WeightEntry
        handleChange={this.handleChange}
        bodyWeight={this.state.bodyWeight}
        height={this.state.height}
        age={this.state.age} />

        <RadioButtonField
        handleChange={this.handleChange}
        gender={this.state.gender}
        level={this.state.level}
        goal={this.state.goal}
        units={this.state.units} />

        <Button variant="contained"
        size="large"
        color="primary"
        className={classNames(classes.button, classes.sideMargin, classes.width)}
        onClick={()=>{
          this.calculateScore();
          }} >
          Calculate
        </Button>

      </div>
    )
  }
}

HomePage.propTypes = {
  handleChange: PropTypes.func,
  calculateScore: PropTypes.func,
};

export default withStyles(styles)(HomePage);
